function toSecondPage(){
    var x = document.getElementById("no-1");
    var y = document.getElementById("no-2");
    if(x.className === "page active"){
        x.className = "page"
        x.className += " page-exit";
        y.className += " active"
    }else{
        x.className = "page active"
    }
}

